import { registerStat } from '../stats'
import { Command } from '../command'
import keycode from 'keycode'
import events from 'dom-events'

module.exports = exports = (...args) => new KeyboardInputCommand(...args)
export class KeyboardInputCommand extends Command {
  constructor(ctx, opts = {}) {
    registerStat('KeyboardInput')

    const keycodes = {}
    const keys = {}

    // update keydown states
    events.on(document, 'keydown', (e) => {
      if (false == ctx.hasFocus) return
      const code = e.which || e.keyCode || e.charCode
      if (null != code) {
        // set key code
        keycodes[code] = true
        // set key name
        keys[keycode(code)] = true
      }
    }, false)

    // update keyup states
    events.on(document, 'keyup', (e) => {
      if (false == ctx.hasFocus) return
      const code = e.which || e.keyCode || e.charCode
      if (null != code) {
        // set key code
        keycodes[code] = false
        // set key name
        keys[keycode(code)] = false
      }
    }, false)

    ctx.on('blur', () => { reset() })
    super((state, block) => {
      if ('function' == typeof state) {
        block = state
        state = {}
      }

      state = state || {}
      block = block || function() {}
      block({ ...state, keycodes, keys })
    })

    function reset() {
      for (let code in keycodes) {
        keycodes[code] = false
      }

      for (let key in keys) {
        keys[key] = false
      }
    }
  }
}

'use strict'

/**
 * Module dependencies.
 */

import { registerStat } from '../stats'
import onMouseChange from 'mouse-change'
import onMouseWheel from 'mouse-wheel'
import { Command } from '../command'
import events from 'dom-events'
import raf from 'raf'

module.exports = exports = (...args) => new MouseInputCommand(...args)
export class MouseInputCommand extends Command {
  constructor(ctx, { allowWheel = true } = {}) {
    registerStat('MouseInput')

    const wheel = {
      currentX: 0, currentY: 0,
      deltaX: 0, deltaY: 0,
      prevX: 0, prevY: 0,
    }

    let currentX = 0
    let currentY = 0
    let buttons = 0
    let deltaX = 0
    let deltaY = 0
    let prevX = 0
    let prevY = 0

    ctx.on('blur', () => { buttons = 0 })

    // focus/blur context on mouse down
    events.on(document, 'mousedown', (e) => {
      if (e.target == ctx.domElement) {
        ctx.focus()
      } else {
        ctx.blur()
      }
    })

    // update state on mouse change and reset
    // delta values on next animation frame
    onMouseChange(ctx.domElement, (b, x, y) => {
      synchronizeMouse(b, x, y)
    })

    // update mouse wheel deltas and then
    // reset them on the next animation frame
    onMouseWheel(ctx.domElement, (dx, dy, dz) => {
      if (false === allowWheel) { return }
      Object.assign(wheel, {
        currentX: wheel.currentX + dx,
        currentY: wheel.currentY + dy,
        currentZ: wheel.currentZ + dz,
        deltaX: dx,
        deltaY: dy,
        deltaZ: dz,
        prevX: wheel.currentX,
        prevY: wheel.currentY,
        prevZ: wheel.currentZ,
      })

      raf(() => Object.assign(wheel, {
        deltaX: 0,
        deltaY: 0,
        deltaZ: 0,
      }))
    })

    super((state, block) => {
      if ('function' == typeof state) {
        block = state
        state = {}
      }

      state = state || {}
      block = block || function() {}

      if ('allowWheel' in state) { allowWheel = state.allowWheel }
      if ('currentX' in state) { currentX = state.currentX }
      if ('currentY' in state) { currentY = state.currentY }

      synchronizeMouse(buttons, currentX, currentY)

      block({
        ...state,
        allowWheel,
        currentX,
        currentY,
        buttons,
        deltaX,
        deltaY,
        prevX,
        prevY,
        wheel,
      })
    })

    function synchronizeMouse(b, x, y) {
      buttons = b

      if (currentX == x && currentY == y) {
        return
      }

      prevX = currentX
      prevY = currentY
      deltaX = x - currentX
      deltaY = y - currentY
      currentX = x
      currentY = y

      raf(() => {
        deltaX = 0
        deltaY = 0
      })
    }
  }
}

'use strict'

/**
 * Module dependencies.
 */

import 'webvr-polyfill'
import { registerStat } from '../stats'
import { Command } from '../command'
import events from 'dom-events'
import quat from 'gl-quat'
import raf from 'raf'

import { computeQuaternion } from '../math/euler'
import { computeEuler } from '../math/quaternion'
import {
  Quaternion,
  Vector,
  Euler,
} from '../math'

import {
  getScreenOrientation,
  radians,
} from '../utils'

// VRFRameData
const vrFrameData = new VRFrameData()
const vrDisplays = []
navigator.getVRDisplays()
.then((displays) => {
  Object.assign(vrDisplays, displays)
})
.catch((err) => {
  console.error(err.stack || err)
})

// Global orientation state object.
const globalState = {
  hasDeviceOrientation: false,
  absolute: null,

  currentAlpha: 0, // z
  currentBeta: 90, // x
  currentGamma: 0, // y

  deltaAlpha: 0,
  deltaBeta: 0,
  deltaGamma: 0,

  prevAlpha: 0,
  prevBeta: 0,
  prevGamma: 0,
}

// update global device orientation state
events.on(window, 'deviceorientation', (e) => {
  // ZXY
  const { alpha, beta, gamma, absolute } = e

  if (null != alpha && null != beta && null != gamma) {
    globalState.hasDeviceOrientation = true
  } else {
    globalState.hasDeviceOrientation = false
    return
  }

  Object.assign(globalState, {
    absolute,

    currentAlpha: alpha,
    currentBeta: beta,
    currentGamma: gamma,

    deltaAlpha: alpha - globalState.currentAlpha,
    deltaBeta: beta - globalState.currentBeta,
    deltaGamma: gamma - globalState.currentGamma,

    prevAlpha: globalState.currentAlpha,
    prevBeta: globalState.currentBeta,
    prevGamma: globalState.currentGamma,
  })

  raf(() => Object.assign(globalState, {
    deltaAlpha: 0,
    deltaBeta: 0,
    deltaGamma: 0,
  }))
})

// expose device motion state
events.on(window, 'devicemotion', (e) => {
  Object.assign(globalState, {
    accelerationIncludingGravity: e.accelerationIncludingGravity,
    acceleration: e.acceleration,
    rotationRate: e.rotationRate,
    interval: e.interval,
  })
})

module.exports = exports = (...args) => new OrientationInputCommand(...args)
export class OrientationInputCommand extends Command {
  constructor(ctx, {} = {}) {
    registerStat('OrienationInput')

    let preferDeviceRotation = false
    const deviceRotation = new Quaternion()
    const deviceEuler = new Euler()

    super((state, block) => {
      if ('function' == typeof state) {
        block = state
        state = {}
      }

      const isEmulated = (
        vrDisplays.length &&
        /emulated/i.test(vrDisplays[0].displayName)
      )

      state = state || {}
