		x = sx * cy * cz + cx * sy * sz
		y = cx * sy * cz - sx * cy * sz
		z = cx * cy * sz + sx * sy * cz
		w = cx * cy * cz - sx * sy * sz
	} else if (order == 'yxz') {
		x = sx * cy * cz + cx * sy * sz
		y = cx * sy * cz - sx * cy * sz
		z = cx * cy * sz - sx * sy * cz
		w = cx * cy * cz + sx * sy * sz
	} else if (order == 'zxy') {
		x = sx * cy * cz - cx * sy * sz
		y = cx * sy * cz + sx * cy * sz
		z = cx * cy * sz + sx * sy * cz
		w = cx * cy * cz - sx * sy * sz
	} else if (order == 'zyx') {
		x = sx * cy * cz - cx * sy * sz
		y = cx * sy * cz + sx * cy * sz
		z = cx * cy * sz - sx * sy * cz
		w = cx * cy * cz + sx * sy * sz
	} else if (order == 'yzx') {
		x = sx * cy * cz + cx * sy * sz
		y = cx * sy * cz + sx * cy * sz
		z = cx * cy * sz - sx * sy * cz
		w = cx * cy * cz - sx * sy * sz
	} else if (order == 'xzy') {
		x = sx * cy * cz - cx * sy * sz
		y = cx * sy * cz - sx * cy * sz
		z = cx * cy * sz + sx * sy * cz
		w = cx * cy * cz + sx * sy * sz
	}

	return [x, y, z, w]
